Ext.define('Tel100.view.document.gnerc.PanelViewModel', {
  extend: 'Ext.app.ViewModel',
  alias: 'viewmodel.documentgnercpanel',

  requires: [
    'Ext.data.Store',
    'Ext.data.proxy.Ajax',
    'Ext.data.reader.Json',
    'Ext.app.bind.Formula'
  ],

  data: {
    type_id: null,
    editable: false,
    showplus: true,
    filename: null,
    mediate: false,
    status: null,
    step: 0,
    send_status: 0
  },

  stores: {
    gnerc: {
      listeners: {
        datachanged: function() {
          var store = this;
          store.viewModel.set('filename', store.data.items[0].get('name'));
          store.viewModel.set('showplus', !store.data.items[0].get('name'));
          store.viewModel.set('type_id', store.data.items[0].get('type_id'));
          store.viewModel.set('mediate', store.data.items[0].get('mediate'));
          store.viewModel.set('status', store.data.items[0].get('status'));
          store.viewModel.set('step', store.data.items[0].get('step'));
          store.viewModel.set('send_status', store.data.items[0].get('send_status'));
        }
      },
      autoLoad: true,
      model: 'Tel100.model.document.Gnerc',
      proxy: {
        type: 'ajax',
        extraParams: {
          document_id: '{document.id}'
        },
        url: '/api/documents/gnerc',
        reader: {
          type: 'json'
        }
      }
    },
    gnerc_subtypes: {
      autoLoad: true,
      model: 'Tel100.model.document.Gnerc_subtypes'
    },
    smshistory: {
      autoLoad: true,
      model: 'Tel100.model.document.Sms',
      proxy: {
        type: 'ajax',
        extraParams: {
          document_id: '{document.id}'
        },
        url: '/api/documents/gnerc/sms',
        reader: {
          type: 'json'
        }
      }
    },
    smses: {
      autoLoad: true,
      model: 'Tel100.model.document.Sms',
      proxy: {
        type: 'ajax',
        extraParams: {
          document_id: '{document.id}'
        },
        url: '/api/documents/gnerc/sms',
        reader: {
          type: 'json'
        }
      }
    }
  },
  formulas: {
    showplus: function(get){
      return !get('gnerc.filename');
    },
    notEditable: function(get) {
      return !get('editable');
    },
    showPlus: function(get){
      if(get('document.is_reply')){
        return ( get('document.as_sender') === 1 || get('document.as_sender') === 2 ) && ( get('step') === 3 ) && get('showplus');
      } else {
        return !get('editable') && ( get('step') === 0 ) && get('showplus');
      }
    },
    showMinus: function(get){
      if(get('document.is_reply')){
        return ( get('document.as_sender') === 1 || get('document.as_sender') === 2 ) && ( get('step') === 3 ) && !get('showplus');
      } else {
        return !get('editable') && ( get('step') === 0 ) && !get('showplus');
      }
    },
    isGnercType4: function(get) {
      return ( get('document.type_id') === 13 && !get('document.is_reply') );
    },
    isReply: function(get){
      return get('document.is_reply');
    },
    showSms: function(get){
      // return get('editable') && get('document.is_reply');
      return get('editable') || 
              ( ( ( get('document.as_sender') === 1 || get('document.as_sender') === 2 ) && get('step') === 3 ) || ( get('document.as_author') === 1 && get('step') === 2 ) );
    },
    smsEditable: function(get){
      // return get('editable') || 
      //        ( ( get('document.as_author') === 1 || get('document.as_signee') === 1 ) && !get('document.is_completed') && !get('document.is_canceled') );
      return get('editable') || 
              ( ( ( get('document.as_sender') === 1 || get('document.as_sender') === 2 )  && get('step') === 3 ) || 
                  ( get('document.as_author') === 1 && get('step') === 0 ) );
    },
    hideSend: function(get){
      return !( get('document.as_sender') === 1 && get('step') === 3 );
    },
    hideStatus: function(get){
      return get('editable') || !get('document.is_reply');
    },
    showAnswerStatus: function(get){
      return get('document.is_reply') && ( get('editable') || ( ( ( get('document.as_sender') === 1 || get('document.as_sender') === 2 ) && get('step') === 3 ) || 
                                                                ( get('document.as_author') === 1 && get('step') === 0 ) ) );
    },
    hideMediate: function(get){
      return ( get('document.type_id') === 15 || !get('document.is_reply') );
    },
    comboHidden: function(get){
      return !get('document.type_id') === 13 ;
    },
    dynamicTitle: function(get){
      if(get('document.is_reply')){
        return i18n.document.base.gnerc_reply;
      } else {
        return i18n.document.base.gnerc;
      }
    },
  }

});